import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircleCheck, faPen, faTrashCan } from '@fortawesome/free-solid-svg-icons';
import './App.css';
import { useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { DndProvider } from 'react-dnd';

const Task = ({ task, index, markDone, deleteTask, updateTask }) => {
  const [{ isDragging }, drag] = useDrag({
    item: { type: 'task', task },
    collect: monitor => ({
      isDragging: monitor.isDragging(),
    }),
  });

  return (
    <div
      className={`col taskBg ${isDragging ? 'dragging' : ''}`}
      ref={drag}
      style={{
        opacity: isDragging ? 0.5 : 1,
        cursor: 'move',
      }}
    >
      <div className={task.status ? 'done' : ''}>
        <span className='taskNumber'>{index + 1}</span>
        <span className='taskText'>{task.title}</span>
      </div>
      <div className="iconsWrap">
        <span title='Done / Not Done ' onClick={() => markDone(task.id)}>
          <FontAwesomeIcon icon={faCircleCheck} />
        </span>
        {task.status ? null : (
          <span
            title='Modify'
            onClick={() =>
              updateTask({
                id: task.id,
                title: task.title,
                status: task.status ? true : false,
              })
            }
          >
            <FontAwesomeIcon icon={faPen} />
          </span>
        )}

        <span title='Delete' onClick={() => deleteTask(task.id)}>
          <FontAwesomeIcon icon={faTrashCan} />
        </span>
      </div>
    </div>
  );
};

function App() {
  const [toDo, setToDo] = useState([
    { id: 1, title: 'create a to-do list app using react.js', status: true },
    { id: 2, title: 'Get A Job', status: false },
  ]);

  const [newTask, setNewTask] = useState('');
  const [updateData, setUpdateData] = useState('');

  const addTask = () => {
    if (newTask) {
      let num = toDo.length + 1;
      let newEntry = { id: num, title: newTask, status: false };
      setToDo([...toDo, newEntry]);
      setNewTask('');
    }
  };

  const deleteTask = (id) => {
    let newTask = toDo.filter((task) => task.id !== id);
    setToDo(newTask);
  };

  const markDone = (id) => {
    let newTask = toDo.map((task) => {
      if (task.id === id) {
        return { ...task, status: !task.status };
      }
      return task;
    });
    setToDo(newTask);
  };

  const cancelUpdate = () => {
    setUpdateData('');
  };

  const changeTask = (e) => {
    let newEntry = {
      id: updateData.id,
      title: e.target.value,
      status: updateData.status ? true : false,
    };
    setUpdateData(newEntry);
  };

  const updateTask = () => {
    let filterRecords = [...toDo].filter((task) => task.id !== updateData.id);
    let updateObject = [...filterRecords, updateData];
    setToDo(updateObject);
    setUpdateData('');
  };

  return (
    <div className='container App'>
      <br />
      <br />
      <h2>To Do App</h2>
      <br />
      <br />
      {updateData && updateData ? (
        <>
          <div className='row'>
            <div className='col-5'>
              <input
                value={updateData && updateData.title}
                onChange={(e) => changeTask(e)}
                className='form-control form-control-lg'
              />
            </div>
            <div className='col-4'>
              <button onClick={updateTask} className='btn btn-lg btn-warning mr-20'>
                Update
              </button>
              <button onClick={cancelUpdate} className='btn btn-lg btn-danger mr-20'>
                Cancel
              </button>
            </div>
          </div>
          <br />
        </>
      ) : (
        <>
          <div className='row'>
            <div className='col-4'>
              <input
                value={newTask}
                onChange={(e) => setNewTask(e.target.value)}
                className='form-control form-control-lg'
              />
            </div>
            <div className='col-5'>
              <button onClick={addTask} className='btn btn-lg btn-success mr-20'>
                New Task
              </button>
            </div>
          </div>
          <br />
        </>
      )}
      <div className='empty-text'>{toDo && toDo.length ? '' : 'Add New Tasks In The Input Field'}</div>
      {toDo &&
        toDo.map((task, index) => (
          <Task
            key={task.id}
            task={task}
            index={index}
            markDone={markDone}
            deleteTask={deleteTask}
            updateTask={setUpdateData}
          />
        ))}
      <p>all rights are reserved to | Yahya Saadaoui</p>
    </div>
  );
}

export default function AppWrapper() {
  return (
    <DndProvider backend={HTML5Backend}>
      <App />
    </DndProvider>
  );
}
